ALTER TABLE events CHANGE event event ENUM('NONE','GIVE_ITEM_PLAYER','TAKE_ITEM_PLAYER','SET_ITEM_PLAYER','GIVE_ITEM_GAME','TAKE_ITEM_GAME','SET_ITEM_GAME','GIVE_ITEM_GROUP','TAKE_ITEM_GROUP','SET_ITEM_GROUP','SET_SCENE','SET_GROUP') NOT NULL DEFAULT 'NONE';
CREATE TABLE groups (group_id INT(32) UNSIGNED NOT NULL AUTO_INCREMENT PRIMARY KEY, game_id INT(32) UNSIGNED NOT NULL, name VARCHAR(255) NOT NULL DEFAULT "", description TEXT NOT NULL, created TIMESTAMP DEFAULT '0000-00-00 00:00:00', last_active TIMESTAMP DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP);
CREATE TABLE user_game_groups (user_id INT(32) UNSIGNED NOT NULL, game_id INT(32) UNSIGNED NOT NULL, group_id INT(32) UNSIGNED NOT NULL, created TIMESTAMP DEFAULT '0000-00-00 00:00:00', last_active TIMESTAMP DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP, PRIMARY KEY (user_id, game_id));
ALTER TABLE instances CHANGE object_type object_type ENUM('NONE','PLAQUE','ITEM','DIALOG','WEB_PAGE','NOTE','FACTORY','SCENE','EVENT_PACKAGE') NOT NULL DEFAULT 'NONE';
ALTER TABLE user_log CHANGE event_type event_type ENUM('NONE','LOG_IN','BEGIN_GAME','RESET_GAME','MOVE','RECEIVE_ITEM','LOSE_ITEM','GAME_RECEIVE_ITEM','GAME_LOSE_ITEM','GROUP_RECEIVE_ITEM','GROUP_LOSE_ITEM','VIEW_TAB','VIEW_INSTANCE','VIEW_PLAQUE','VIEW_ITEM','VIEW_DIALOG','VIEW_DIALOG_SCRIPT','VIEW_WEB_PAGE','VIEW_NOTE','TRIGGER_TRIGGER','CHANGE_SCENE','RUN_EVENT_PACKAGE','COMPLETE_QUEST','CREATE_NOTE','GIVE_NOTE_LIKE','GET_NOTE_LIKE','GIVE_NOTE_COMMENT','GET_NOTE_COMMENT','UPLOAD_MEDIA_ITEM','UPLOAD_MEDIA_ITEM_IMAGE','UPLOAD_MEDIA_ITEM_AUDIO','UPLOAD_MEDIA_ITEM_VIDEO','RECEIVE_WEBHOOK','SEND_WEBHOOK') NOT NULL DEFAULT 'NONE';
