CREATE TABLE `fields` ( `field_id` int(11) unsigned NOT NULL AUTO_INCREMENT, `game_id` int(11) unsigned NOT NULL, `field_type` enum('TEXT','TEXTAREA','SINGLESELECT','MULTISELECT','MEDIA','NOMEN') NOT NULL DEFAULT 'TEXT', `label` text NOT NULL, PRIMARY KEY (`field_id`), KEY `field_game_id` (`game_id`) ) ENGINE=InnoDB DEFAULT CHARSET=utf8;
CREATE TABLE `field_options` ( `field_option_id` int(11) unsigned NOT NULL AUTO_INCREMENT, `field_id` int(11) unsigned NOT NULL, `game_id` int(11) unsigned NOT NULL, `option` text NOT NULL, PRIMARY KEY (`field_option_id`), KEY `field_option_index` (`game_id`,`field_id`) ) ENGINE=InnoDB DEFAULT CHARSET=utf8;
CREATE TABLE `field_data` ( `field_data_id` int(11) unsigned NOT NULL AUTO_INCREMENT, `note_id` int(11) unsigned NOT NULL, `field_id` int(11) unsigned NOT NULL, `field_data` text, `media_id` int(11) unsigned NOT NULL, `field_option_id` int(11) unsigned NOT NULL, PRIMARY KEY (`field_data_id`), KEY `field_data_note` (`note_id`) ) ENGINE=InnoDB DEFAULT CHARSET=utf8;
